contracts/staked-usde/StakedUSDe.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./interfaces/IStakedUSDeCooldown.sol";
import "./USDeSilo.sol";

/**
 * @title StakedUSDe
 * @notice Staking contract for USDe tokens with OVault integration and cooldown functionality (V2)
 * @dev Users stake USDe tokens and earn rewards. The contract uses ERC4626 standard
 *      for vault operations and can be integrated with LayerZero's OVault for omnichain functionality.
 *
 *      Rewards are distributed by REWARDER_ROLE and vest over time to prevent MEV attacks.
 *
 * @dev If cooldown duration is set to zero, the StakedUSDe behavior follows ERC4626 standard
 *      and disables cooldownShares and cooldownAssets methods. If cooldown duration is greater
 *      than zero, the ERC4626 withdrawal and redeem functions are disabled, breaking the ERC4626
 *      standard, and enabling the cooldownShares and the cooldownAssets functions.
 */
contract StakedUSDe is AccessControl, ReentrancyGuard, ERC20Permit, ERC4626, IStakedUSDeCooldown, Pausable {
    using SafeERC20 for IERC20;

    /* --------------- CONSTANTS --------------- */

    /// @notice Role that can distribute rewards to this contract
    bytes32 public constant REWARDER_ROLE = keccak256("REWARDER_ROLE");

    /// @notice Role that can blacklist and un-blacklist addresses
    bytes32 public constant BLACKLIST_MANAGER_ROLE = keccak256("BLACKLIST_MANAGER_ROLE");

    /// @notice Role that prevents an address from staking
    bytes32 public constant SOFT_RESTRICTED_STAKER_ROLE = keccak256("SOFT_RESTRICTED_STAKER_ROLE");

    /// @notice Role that prevents an address from transferring, staking, or unstaking
    bytes32 public constant FULL_RESTRICTED_STAKER_ROLE = keccak256("FULL_RESTRICTED_STAKER_ROLE");

    /// @notice Vesting period for rewards (8 hours)
    uint256 public constant VESTING_PERIOD = 8 hours;

    /// @notice Minimum non-zero shares to prevent donation attack
    uint256 public constant MIN_SHARES = 1 ether;

    /// @notice Maximum cooldown duration (90 days)
    uint24 public constant MAX_COOLDOWN_DURATION = 90 days;

    /* --------------- STATE VARIABLES --------------- */

    /// @notice Amount of the last asset distribution + unvested remainder
    uint256 public vestingAmount;

    /// @notice Timestamp of the last asset distribution
    uint256 public lastDistributionTimestamp;

    /// @notice Cooldown duration in seconds
    uint24 public cooldownDuration;

    /// @notice Mapping of user addresses to their cooldown data
    mapping(address => UserCooldown) public cooldowns;

    /// @notice Silo contract for holding assets during cooldown
    USDeSilo public immutable silo;

    /* --------------- MODIFIERS --------------- */

    /// @notice Ensure input amount is non-zero
    modifier notZero(uint256 amount) {
        if (amount == 0) revert InvalidAmount();
        _;
    }

    /// @notice Ensure blacklist target is not admin
    modifier notAdmin(address target) {
        if (hasRole(DEFAULT_ADMIN_ROLE, target)) revert CantBlacklistOwner();
        _;
    }

    /// @notice Ensure cooldownDuration is zero
    modifier ensureCooldownOff() {
        if (cooldownDuration != 0) revert OperationNotAllowed();
        _;
    }

    /// @notice Ensure cooldownDuration is gt 0
    modifier ensureCooldownOn() {
        if (cooldownDuration == 0) revert OperationNotAllowed();
        _;
    }

    /* --------------- CONSTRUCTOR --------------- */

    /**
     * @notice Constructor for StakedUSDe contract
     * @param _asset The address of the USDe token
     * @param _initialRewarder The address of the initial rewarder
     * @param _admin The address of the admin role
     */
    constructor(
        IERC20 _asset,
        address _initialRewarder,
        address _admin
    ) ERC20("Staked USDe", "sUSDe") ERC4626(_asset) ERC20Permit("sUSDe") {
        if (_admin == address(0) || _initialRewarder == address(0) || address(_asset) == address(0)) {
            revert InvalidZeroAddress();
        }

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(REWARDER_ROLE, _initialRewarder);
        _grantRole(BLACKLIST_MANAGER_ROLE, _admin);

        silo = new USDeSilo(address(this), address(_asset));
        cooldownDuration = MAX_COOLDOWN_DURATION;
    }

    /* --------------- EXTERNAL --------------- */

    /**
     * @notice Transfer rewards from the rewarder into this contract
     * @param amount The amount of rewards to transfer
     */
    function transferInRewards(
        uint256 amount
    ) external nonReentrant whenNotPaused onlyRole(REWARDER_ROLE) notZero(amount) {
        _updateVestingAmount(amount);

        // Transfer assets from rewarder to this contract
        IERC20(asset()).safeTransferFrom(msg.sender, address(this), amount);

        emit RewardsReceived(amount);
    }

    /**
     * @notice Add an address to blacklist
     * @param target The address to blacklist
     * @param isFullBlacklisting Soft or full blacklisting level
     */
    function addToBlacklist(
        address target,
        bool isFullBlacklisting
    ) external onlyRole(BLACKLIST_MANAGER_ROLE) notAdmin(target) {
        bytes32 role = isFullBlacklisting ? FULL_RESTRICTED_STAKER_ROLE : SOFT_RESTRICTED_STAKER_ROLE;
        _grantRole(role, target);
    }

    /**
     * @notice Remove an address from blacklist
     * @param target The address to un-blacklist
     * @param isFullBlacklisting Soft or full blacklisting level
     */
    function removeFromBlacklist(address target, bool isFullBlacklisting) external onlyRole(BLACKLIST_MANAGER_ROLE) {
        bytes32 role = isFullBlacklisting ? FULL_RESTRICTED_STAKER_ROLE : SOFT_RESTRICTED_STAKER_ROLE;
        _revokeRole(role, target);
    }

    /**
     * @notice Rescue tokens accidentally sent to the contract
     * @dev Cannot rescue the underlying asset (USDe)
     * @param token The token to be rescued
     * @param amount The amount of tokens to be rescued
     * @param to Where to send rescued tokens
     */
    function rescueTokens(
        address token,
        uint256 amount,
        address to
    ) external nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
        if (address(token) == asset()) revert InvalidToken();
        IERC20(token).safeTransfer(to, amount);
    }

    /**
     * @notice Redistribute locked amount from full restricted user
     * @param from The address to burn the entire balance from (must have FULL_RESTRICTED_STAKER_ROLE)
     * @param to The address to mint the entire balance to (or address(0) to burn)
     */
    function redistributeLockedAmount(address from, address to) external nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
        if (hasRole(FULL_RESTRICTED_STAKER_ROLE, from) && !hasRole(FULL_RESTRICTED_STAKER_ROLE, to)) {
            uint256 amountToDistribute = balanceOf(from);
            uint256 usdeToVest = previewRedeem(amountToDistribute);

            _burn(from, amountToDistribute);

            // to address of address(0) enables burning
            if (to == address(0)) {
                _updateVestingAmount(usdeToVest);
            } else {
                _mint(to, amountToDistribute);
            }

            emit LockedAmountRedistributed(from, to, amountToDistribute);
        } else {
            revert OperationNotAllowed();
        }
    }

    /**
     * @dev See {IERC4626-withdraw}.
     * @notice Only enabled when cooldown is off
     */
    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    ) public virtual override(ERC4626, IERC4626) whenNotPaused ensureCooldownOff returns (uint256) {
        return super.withdraw(assets, receiver, owner);
    }

    /**
     * @dev See {IERC4626-redeem}.
     * @notice Only enabled when cooldown is off
     */
    function redeem(
        uint256 shares,
        address receiver,
        address owner
    ) public virtual override(ERC4626, IERC4626) whenNotPaused ensureCooldownOff returns (uint256) {
        return super.redeem(shares, receiver, owner);
    }

    /**
     * @notice Claim the staking amount after the cooldown has finished
     * @dev unstake can be called after cooldown have been set to 0, to let accounts claim remaining assets locked at Silo
     * @param receiver Address to send the assets by the staker
     */
    function unstake(address receiver) external whenNotPaused {
        UserCooldown storage userCooldown = cooldowns[msg.sender];
        uint256 assets = userCooldown.underlyingAmount;

        if (block.timestamp >= userCooldown.cooldownEnd || cooldownDuration == 0) {
            userCooldown.cooldownEnd = 0;
            userCooldown.underlyingAmount = 0;

            silo.withdraw(receiver, assets);
        } else {
            revert InvalidCooldown();
        }
    }

    /**
     * @notice Redeem assets and starts a cooldown to claim the converted underlying asset
     * @param assets assets to redeem
     */
    function cooldownAssets(uint256 assets) external whenNotPaused ensureCooldownOn returns (uint256 shares) {
        if (assets > maxWithdraw(msg.sender)) revert ExcessiveWithdrawAmount();

        shares = previewWithdraw(assets);

        cooldowns[msg.sender].cooldownEnd = uint104(block.timestamp) + cooldownDuration;
        cooldowns[msg.sender].underlyingAmount += uint152(assets);

        _withdraw(msg.sender, address(silo), msg.sender, assets, shares);
    }

    /**
     * @notice Redeem shares into assets and starts a cooldown to claim the converted underlying asset
     * @param shares shares to redeem
     */
    function cooldownShares(uint256 shares) external whenNotPaused ensureCooldownOn returns (uint256 assets) {
        if (shares > maxRedeem(msg.sender)) revert ExcessiveRedeemAmount();

        assets = previewRedeem(shares);

        cooldowns[msg.sender].cooldownEnd = uint104(block.timestamp) + cooldownDuration;
        cooldowns[msg.sender].underlyingAmount += uint152(assets);

        _withdraw(msg.sender, address(silo), msg.sender, assets, shares);
    }

    /**
     * @notice Set cooldown duration
     * @dev If cooldown duration is set to zero, the behavior follows ERC4626 standard and disables
     *      cooldownShares and cooldownAssets methods. If cooldown duration is greater than zero,
     *      the ERC4626 withdrawal and redeem functions are disabled, breaking the ERC4626 standard,
     *      and enabling the cooldownShares and the cooldownAssets functions.
     * @param duration Duration of the cooldown
     */
    function setCooldownDuration(uint24 duration) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (duration > MAX_COOLDOWN_DURATION) {
            revert InvalidCooldown();
        }

        uint24 previousDuration = cooldownDuration;
        cooldownDuration = duration;
        emit CooldownDurationUpdated(previousDuration, cooldownDuration);
    }

    /**
     * @notice Pause all deposits, withdrawals, and cooldown operations
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @notice Unpause all deposits, withdrawals, and cooldown operations
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /* --------------- PUBLIC --------------- */

    /**
     * @notice Returns the amount of USDe tokens that are vested in the contract
     * @return The total vested assets
     */
    function totalAssets() public view override(ERC4626, IERC4626) returns (uint256) {
        return IERC20(asset()).balanceOf(address(this)) - getUnvestedAmount();
    }

    /**
     * @notice Returns the amount of USDe tokens that are unvested in the contract
     * @return The unvested amount
     */
    function getUnvestedAmount() public view returns (uint256) {
        uint256 timeSinceLastDistribution = block.timestamp - lastDistributionTimestamp;

        if (timeSinceLastDistribution >= VESTING_PERIOD) {
            return 0;
        }

        uint256 deltaT;
        unchecked {
            deltaT = (VESTING_PERIOD - timeSinceLastDistribution);
        }
        return (deltaT * vestingAmount) / VESTING_PERIOD;
    }

    /// @dev Necessary because both ERC20 (from ERC20Permit) and ERC4626 declare decimals()
    function decimals() public pure override(ERC4626, ERC20, IERC20Metadata) returns (uint8) {
        return 18;
    }

    /// @dev Override nonces to resolve conflict between ERC20Permit and other base classes
    function nonces(address owner) public view virtual override(ERC20Permit, IERC20Permit) returns (uint256) {
        return super.nonces(owner);
    }

    /* --------------- INTERNAL --------------- */

    /// @notice Ensure minimum shares to prevent donation attack
    function _checkMinShares() internal view {
        uint256 _totalSupply = totalSupply();
        if (_totalSupply > 0 && _totalSupply < MIN_SHARES) revert MinSharesViolation();
    }

    /**
     * @dev Deposit/mint common workflow with blacklist checks
     */
    function _deposit(
        address caller,
        address receiver,
        uint256 assets,
        uint256 shares
    ) internal override nonReentrant notZero(assets) notZero(shares) {
        if (hasRole(SOFT_RESTRICTED_STAKER_ROLE, caller) || hasRole(SOFT_RESTRICTED_STAKER_ROLE, receiver)) {
            revert OperationNotAllowed();
        }
        super._deposit(caller, receiver, assets, shares);
        _checkMinShares();
    }

    /**
     * @dev Withdraw/redeem common workflow with blacklist checks
     */
    function _withdraw(
        address caller,
        address receiver,
        address owner,
        uint256 assets,
        uint256 shares
    ) internal override nonReentrant notZero(assets) notZero(shares) {
        if (
            hasRole(FULL_RESTRICTED_STAKER_ROLE, caller) ||
            hasRole(FULL_RESTRICTED_STAKER_ROLE, receiver) ||
            hasRole(FULL_RESTRICTED_STAKER_ROLE, owner)
        ) {
            revert OperationNotAllowed();
        }

        super._withdraw(caller, receiver, owner, assets, shares);
        _checkMinShares();
    }

    /**
     * @notice Update vesting amount and timestamp
     * @param newVestingAmount The new vesting amount to add
     */
    function _updateVestingAmount(uint256 newVestingAmount) internal {
        if (getUnvestedAmount() > 0) revert StillVesting();

        vestingAmount = newVestingAmount;
        lastDistributionTimestamp = block.timestamp;
    }

    /**
     * @dev Hook that is called before any transfer of tokens
     * @dev Disables transfers from or to addresses with FULL_RESTRICTED_STAKER_ROLE
     */
    function _update(address from, address to, uint256 value) internal virtual override {
        if (hasRole(FULL_RESTRICTED_STAKER_ROLE, from) && to != address(0)) {
            revert OperationNotAllowed();
        }
        if (hasRole(FULL_RESTRICTED_STAKER_ROLE, to)) {
            revert OperationNotAllowed();
        }
        super._update(from, to, value);
    }

    /**
     * @dev Prevent renouncing roles to maintain contract security
     */
    function renounceRole(bytes32 role, address account) public virtual override {
        if (role == DEFAULT_ADMIN_ROLE) revert CantRenounceOwnership();
        super.renounceRole(role, account);
    }
}

contracts/staked-usde/StakingRewardsDist.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "./StakedUSDe.sol";
import "../interfaces/usde/IUSDe.sol";

/**
 * @title StakingRewardsDistributor
 * @notice Helper contract to automate staking rewards distribution
 * @dev This contract allows automated reward distribution without multisig transactions,
 *      increasing distribution frequency and reducing arbitrage opportunities.
 *
 *      Roles:
 *      - Owner (multisig): Configuration calls only
 *      - Operator (delegated signer): Can transfer rewards to staking contract
 */
contract StakingRewardsDistributor is Ownable2Step, ReentrancyGuard {
    using SafeERC20 for IERC20;

    /* --------------- CONSTANTS --------------- */

    /// @notice Placeholder address for native ETH
    address internal constant ETH_ADDRESS = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    /* --------------- IMMUTABLES --------------- */

    /// @notice Staking vault contract
    StakedUSDe public immutable STAKING_VAULT;

    /// @notice USDe token
    IERC20 public immutable USDE_TOKEN;

    /* --------------- STATE VARIABLES --------------- */

    /// @notice Operator address authorized to invoke transferInRewards
    address public operator;

    /* --------------- EVENTS --------------- */

    event TokensRescued(address indexed token, address indexed to, uint256 amount);
    event OperatorUpdated(address indexed newOperator, address indexed previousOperator);

    /* --------------- ERRORS --------------- */

    error InvalidZeroAddress();
    error InvalidAmount();
    error TransferFailed();
    error CantRenounceOwnership();
    error OnlyOperator();
    error InsufficientFunds();

    /* --------------- CONSTRUCTOR --------------- */

    /**
     * @notice Constructor for StakingRewardsDistributor
     * @param _stakingVault The staking vault contract
     * @param _usde The USDe token contract
     * @param _admin The admin address (multisig)
     * @param _operator The operator address (delegated signer)
     */
    constructor(StakedUSDe _stakingVault, IERC20 _usde, address _admin, address _operator) Ownable(msg.sender) {
        if (address(_stakingVault) == address(0)) revert InvalidZeroAddress();
        if (address(_usde) == address(0)) revert InvalidZeroAddress();
        if (_admin == address(0)) revert InvalidZeroAddress();
        if (_operator == address(0)) revert InvalidZeroAddress();

        STAKING_VAULT = _stakingVault;
        USDE_TOKEN = _usde;

        _transferOwnership(msg.sender);

        // Set the operator
        setOperator(_operator);

        // Approve USDe to the staking contract
        USDE_TOKEN.safeIncreaseAllowance(address(STAKING_VAULT), type(uint256).max);

        if (msg.sender != _admin) {
            _transferOwnership(_admin);
        }
    }

    /* --------------- EXTERNAL --------------- */

    /**
     * @notice Transfer USDe rewards to the staking contract
     * @param _rewardsAmount The amount of USDe to send
     * @dev Only the operator can call this function
     * @dev This contract must have REWARDER_ROLE in the staking contract
     */
    function transferInRewards(uint256 _rewardsAmount) external nonReentrant {
        if (msg.sender != operator) revert OnlyOperator();

        // Check that this contract holds enough USDe balance
        if (USDE_TOKEN.balanceOf(address(this)) < _rewardsAmount) revert InsufficientFunds();

        STAKING_VAULT.transferInRewards(_rewardsAmount);
    }

    /**
     * @notice Rescue tokens accidentally sent to the contract
     * @param _token The token to rescue (or ETH_ADDRESS for native ETH)
     * @param _to The address to send rescued tokens to
     * @param _amount The amount to rescue
     */
    function rescueTokens(address _token, address _to, uint256 _amount) external onlyOwner {
        if (_to == address(0)) revert InvalidZeroAddress();
        if (_amount == 0) revert InvalidAmount();

        if (_token == ETH_ADDRESS) {
            (bool success, ) = _to.call{ value: _amount }("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(_token).safeTransfer(_to, _amount);
        }

        emit TokensRescued(_token, _to, _amount);
    }

    /**
     * @notice Set a new operator
     * @param _newOperator The new operator address
     */
    function setOperator(address _newOperator) public onlyOwner {
        if (_newOperator == address(0)) revert InvalidZeroAddress();

        address previousOperator = operator;
        operator = _newOperator;

        emit OperatorUpdated(_newOperator, previousOperator);
    }

    /**
     * @notice Prevent renouncing ownership
     */
    function renounceOwnership() public view override onlyOwner {
        revert CantRenounceOwnership();
    }

    /**
     * @notice Receive native ETH
     */
    receive() external payable {}
}

contracts/staked-usde/USDeSilo.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IUSDeSiloDefinitions.sol";

/**
 * @title USDeSilo
 * @notice The Silo allows to store USDe during the stake cooldown process.
 */
contract USDeSilo is IUSDeSiloDefinitions {
    address immutable STAKING_VAULT;
    IERC20 immutable USDE;

    constructor(address stakingVault, address usde) {
        STAKING_VAULT = stakingVault;
        USDE = IERC20(usde);
    }

    modifier onlyStakingVault() {
        if (msg.sender != STAKING_VAULT) revert OnlyStakingVault();
        _;
    }

    function withdraw(address to, uint256 amount) external onlyStakingVault {
        USDE.transfer(to, amount);
    }
}

contracts/usde/USDe.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../mct/MultiCollateralToken.sol";
import "./USDeRedeemSilo.sol";

/**
 * @title USDe
 * @notice Omnichain vault version of USDe with integrated minting functionality and redemption cooldown
 * @dev This contract combines ERC4626 vault with direct collateral minting
 * - Underlying asset: MCT (MultiCollateralToken)
 * - Exchange rate: 1:1 with MCT
 * - Users can mint by depositing collateral (USDC, etc.)
 * - Collateral is converted to MCT, then USDe shares are minted
 * - Redemptions require cooldown: lock USDe  wait X days  redeem to collateral  release
 * - Users can cancel redemption requests during cooldown period
 */
contract USDe is ERC4626, ERC20Permit, AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    /* --------------- CONSTANTS --------------- */

    /// @notice Role for emergency actions
    bytes32 public constant GATEKEEPER_ROLE = keccak256("GATEKEEPER_ROLE");

    /// @notice Role for managing collateral operations
    bytes32 public constant COLLATERAL_MANAGER_ROLE = keccak256("COLLATERAL_MANAGER_ROLE");

    /// @notice Maximum cooldown duration (90 days)
    uint24 public constant MAX_COOLDOWN_DURATION = 90 days;

    /* --------------- STRUCTS --------------- */

    /// @notice Redemption request structure
    struct RedemptionRequest {
        uint104 cooldownEnd; // Timestamp when cooldown ends
        uint152 usdeAmount; // Amount of USDe locked for redemption
        address collateralAsset; // Collateral asset to receive
    }

    /* --------------- STATE VARIABLES --------------- */

    /// @notice The MCT token (underlying asset)
    MultiCollateralToken public immutable mct;

    /// @notice USDe minted per block
    mapping(uint256 => uint256) public mintedPerBlock;

    /// @notice USDe redeemed per block
    mapping(uint256 => uint256) public redeemedPerBlock;

    /// @notice Max minted USDe allowed per block
    uint256 public maxMintPerBlock;

    /// @notice Max redeemed USDe allowed per block
    uint256 public maxRedeemPerBlock;

    /// @notice Delegated signer status for smart contracts
    mapping(address => mapping(address => DelegatedSignerStatus)) public delegatedSigner;

    /// @notice Cooldown duration in seconds for redemptions
    uint24 public cooldownDuration;

    /// @notice Mapping of user addresses to their redemption requests
    mapping(address => RedemptionRequest) public redemptionRequests;

    /// @notice Silo contract for holding locked USDe during cooldown
    USDeRedeemSilo public immutable redeemSilo;

    /* --------------- ENUMS --------------- */

    enum DelegatedSignerStatus {
        REJECTED,
        PENDING,
        ACCEPTED
    }

    /* --------------- EVENTS --------------- */

    event Mint(
        address indexed beneficiary,
        address indexed collateralAsset,
        uint256 collateralAmount,
        uint256 usdeAmount
    );
    event Redeem(
        address indexed beneficiary,
        address indexed collateralAsset,
        uint256 usdeAmount,
        uint256 collateralAmount
    );
    event MaxMintPerBlockChanged(uint256 oldMax, uint256 newMax);
    event MaxRedeemPerBlockChanged(uint256 oldMax, uint256 newMax);
    event DelegatedSignerInitiated(address indexed delegateTo, address indexed delegatedBy);
    event DelegatedSignerAdded(address indexed signer, address indexed delegatedBy);
    event DelegatedSignerRemoved(address indexed signer, address indexed delegatedBy);
    event CooldownDurationUpdated(uint24 previousDuration, uint24 newDuration);
    event RedemptionRequested(
        address indexed user,
        uint256 usdeAmount,
        address indexed collateralAsset,
        uint256 cooldownEnd
    );
    event RedemptionCompleted(
        address indexed user,
        uint256 usdeAmount,
        address indexed collateralAsset,
        uint256 collateralAmount
    );
    event RedemptionCancelled(address indexed user, uint256 usdeAmount);

    /* --------------- ERRORS --------------- */

    error ZeroAddressException();
    error InvalidAmount();
    error UnsupportedAsset();
    error MaxMintPerBlockExceeded();
    error MaxRedeemPerBlockExceeded();
    error InvalidSignature();
    error DelegationNotInitiated();
    error CantRenounceOwnership();
    error InvalidCooldown();
    error NoRedemptionRequest();
    error CooldownNotFinished();
    error ExistingRedemptionRequest();

    /* --------------- MODIFIERS --------------- */

    /// @notice Ensure minted amount doesn't exceed max per block
    modifier belowMaxMintPerBlock(uint256 mintAmount) {
        if (mintedPerBlock[block.number] + mintAmount > maxMintPerBlock) {
            revert MaxMintPerBlockExceeded();
        }
        _;
    }

    /// @notice Ensure redeemed amount doesn't exceed max per block
    modifier belowMaxRedeemPerBlock(uint256 redeemAmount) {
        if (redeemedPerBlock[block.number] + redeemAmount > maxRedeemPerBlock) {
            revert MaxRedeemPerBlockExceeded();
        }
        _;
    }

    /* --------------- CONSTRUCTOR --------------- */

    constructor(
        MultiCollateralToken _mct,
        address admin,
        uint256 _maxMintPerBlock,
        uint256 _maxRedeemPerBlock
    ) ERC20("USDe", "USDe") ERC4626(IERC20(address(_mct))) ERC20Permit("USDe") {
        if (address(_mct) == address(0)) revert ZeroAddressException();
        if (admin == address(0)) revert ZeroAddressException();

        mct = _mct;

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(GATEKEEPER_ROLE, admin);
        _grantRole(COLLATERAL_MANAGER_ROLE, admin);

        _setMaxMintPerBlock(_maxMintPerBlock);
        _setMaxRedeemPerBlock(_maxRedeemPerBlock);

        // Create silo for holding locked USDe during redemption cooldown
        redeemSilo = new USDeRedeemSilo(address(this), address(this));

        // Default cooldown to 7 days
        cooldownDuration = 7 days;
    }

    /* --------------- EXTERNAL MINT/REDEEM --------------- */

    /**
     * @notice Mint USDe by depositing collateral
     * @param collateralAsset The collateral asset to deposit
     * @param collateralAmount The amount of collateral to deposit
     * @return usdeAmount The amount of USDe minted
     */
    function mintWithCollateral(
        address collateralAsset,
        uint256 collateralAmount
    ) external nonReentrant whenNotPaused returns (uint256 usdeAmount) {
        return _mintWithCollateral(collateralAsset, collateralAmount, msg.sender);
    }

    /**
     * @notice Mint USDe on behalf of a beneficiary
     * @param collateralAsset The collateral asset to deposit
     * @param collateralAmount The amount of collateral to deposit
     * @param beneficiary The address to receive minted USDe
     * @return usdeAmount The amount of USDe minted
     */
    function mintWithCollateralFor(
        address collateralAsset,
        uint256 collateralAmount,
        address beneficiary
    ) external nonReentrant whenNotPaused returns (uint256 usdeAmount) {
        if (delegatedSigner[msg.sender][beneficiary] != DelegatedSignerStatus.ACCEPTED) {
            revert InvalidSignature();
        }
        return _mintWithCollateral(collateralAsset, collateralAmount, beneficiary);
    }

    /* --------------- COOLDOWN REDEMPTION --------------- */

    /**
     * @notice Request redemption with cooldown - locks USDe and starts cooldown timer
     * @param collateralAsset The collateral asset to receive after cooldown
     * @param usdeAmount The amount of USDe to lock for redemption
     */
    function cooldownRedeem(address collateralAsset, uint256 usdeAmount) external nonReentrant whenNotPaused {
        if (usdeAmount == 0) revert InvalidAmount();
        if (!mct.isSupportedAsset(collateralAsset)) revert UnsupportedAsset();
        if (redemptionRequests[msg.sender].usdeAmount > 0) revert ExistingRedemptionRequest();

        // Transfer USDe from user to silo (locks it)
        _transfer(msg.sender, address(redeemSilo), usdeAmount);

        // Record redemption request
        redemptionRequests[msg.sender] = RedemptionRequest({
            cooldownEnd: uint104(block.timestamp + cooldownDuration),
            usdeAmount: uint152(usdeAmount),
            collateralAsset: collateralAsset
        });

        emit RedemptionRequested(msg.sender, usdeAmount, collateralAsset, block.timestamp + cooldownDuration);
    }

    /**
     * @notice Complete redemption after cooldown period - redeems USDe for collateral
     */
    function completeRedeem() external nonReentrant whenNotPaused returns (uint256 collateralAmount) {
        RedemptionRequest memory request = redemptionRequests[msg.sender];

        if (request.usdeAmount == 0) revert NoRedemptionRequest();
        if (block.timestamp < request.cooldownEnd) revert CooldownNotFinished();

        uint256 usdeAmount = request.usdeAmount;
        address collateralAsset = request.collateralAsset;

        // Clear redemption request
        delete redemptionRequests[msg.sender];

        // Withdraw USDe from silo back to this contract
        redeemSilo.withdraw(address(this), usdeAmount);

        // Convert USDe amount to collateral amount
        collateralAmount = _convertToCollateralAmount(collateralAsset, usdeAmount);

        // Burn USDe (now it's in this contract)
        _burn(address(this), usdeAmount);

        // Approve MCT to burn
        IERC20(address(mct)).safeIncreaseAllowance(address(mct), usdeAmount);

        // Redeem MCT for collateral and send to user
        uint256 receivedCollateral = mct.redeem(collateralAsset, usdeAmount, msg.sender);

        emit RedemptionCompleted(msg.sender, usdeAmount, collateralAsset, receivedCollateral);
    }

    /**
     * @notice Cancel redemption request and return locked USDe to user
     */
    function cancelRedeem() external nonReentrant {
        RedemptionRequest memory request = redemptionRequests[msg.sender];

        if (request.usdeAmount == 0) revert NoRedemptionRequest();

        uint256 usdeAmount = request.usdeAmount;

        // Clear redemption request
        delete redemptionRequests[msg.sender];

        // Return USDe from silo to user
        redeemSilo.withdraw(msg.sender, usdeAmount);

        emit RedemptionCancelled(msg.sender, usdeAmount);
    }

    /* --------------- ADMIN FUNCTIONS --------------- */

    /**
     * @notice Set max mint per block
     * @param _maxMintPerBlock New max mint per block
     */
    function setMaxMintPerBlock(uint256 _maxMintPerBlock) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setMaxMintPerBlock(_maxMintPerBlock);
    }

    /**
     * @notice Set max redeem per block
     * @param _maxRedeemPerBlock New max redeem per block
     */
    function setMaxRedeemPerBlock(uint256 _maxRedeemPerBlock) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setMaxRedeemPerBlock(_maxRedeemPerBlock);
    }

    /**
     * @notice Disable mint and redeem in emergency
     */
    function disableMintRedeem() external onlyRole(GATEKEEPER_ROLE) {
        _setMaxMintPerBlock(0);
        _setMaxRedeemPerBlock(0);
    }

    /**
     * @notice Set cooldown duration for redemptions
     * @param duration New cooldown duration (max 90 days)
     */
    function setCooldownDuration(uint24 duration) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (duration > MAX_COOLDOWN_DURATION) {
            revert InvalidCooldown();
        }

        uint24 previousDuration = cooldownDuration;
        cooldownDuration = duration;
        emit CooldownDurationUpdated(previousDuration, cooldownDuration);
    }

    /**
     * @notice Pause all mint and redeem operations
     */
    function pause() external onlyRole(GATEKEEPER_ROLE) {
        _pause();
    }

    /**
     * @notice Unpause all mint and redeem operations
     */
    function unpause() external onlyRole(GATEKEEPER_ROLE) {
        _unpause();
    }

    /* --------------- DELEGATED SIGNER FUNCTIONS --------------- */

    /**
     * @notice Enable smart contracts to delegate signing
     * @param _delegateTo The address to delegate to
     */
    function setDelegatedSigner(address _delegateTo) external {
        delegatedSigner[_delegateTo][msg.sender] = DelegatedSignerStatus.PENDING;
        emit DelegatedSignerInitiated(_delegateTo, msg.sender);
    }

    /**
     * @notice Confirm delegation
     * @param _delegatedBy The address that initiated delegation
     */
    function confirmDelegatedSigner(address _delegatedBy) external {
        if (delegatedSigner[msg.sender][_delegatedBy] != DelegatedSignerStatus.PENDING) {
            revert DelegationNotInitiated();
        }
        delegatedSigner[msg.sender][_delegatedBy] = DelegatedSignerStatus.ACCEPTED;
        emit DelegatedSignerAdded(msg.sender, _delegatedBy);
    }

    /**
     * @notice Remove delegated signer
     * @param _removedSigner The address to remove
     */
    function removeDelegatedSigner(address _removedSigner) external {
        delegatedSigner[_removedSigner][msg.sender] = DelegatedSignerStatus.REJECTED;
        emit DelegatedSignerRemoved(_removedSigner, msg.sender);
    }

    /* --------------- INTERNAL --------------- */

    /**
     * @notice Internal mint logic with collateral
     * @param collateralAsset The collateral asset
     * @param collateralAmount The amount of collateral
     * @param beneficiary The address to receive USDe
     * @return usdeAmount The amount of USDe minted
     */
    function _mintWithCollateral(
        address collateralAsset,
        uint256 collateralAmount,
        address beneficiary
    ) internal belowMaxMintPerBlock(collateralAmount) returns (uint256 usdeAmount) {
        if (collateralAmount == 0) revert InvalidAmount();
        if (!mct.isSupportedAsset(collateralAsset)) revert UnsupportedAsset();
        if (beneficiary == address(0)) revert ZeroAddressException();

        // Convert collateral to USDe amount (normalize decimals)
        usdeAmount = _convertToUsdeAmount(collateralAsset, collateralAmount);

        // Track minted amount
        mintedPerBlock[block.number] += usdeAmount;

        // Transfer collateral from user to this contract
        IERC20(collateralAsset).safeTransferFrom(beneficiary, address(this), collateralAmount);

        // Approve MCT to spend collateral
        IERC20(collateralAsset).safeIncreaseAllowance(address(mct), collateralAmount);

        // Mint MCT by depositing collateral
        uint256 mctAmount = mct.mint(collateralAsset, collateralAmount, address(this));

        // Mint USDe shares (1:1 with MCT due to ERC4626)
        // Since we control the exchange rate to be 1:1, we mint directly
        _mint(beneficiary, mctAmount);

        emit Mint(beneficiary, collateralAsset, collateralAmount, usdeAmount);
    }

    /**
     * @notice Convert collateral amount to USDe amount (normalize decimals to 18)
     * @param collateralAsset The collateral asset address
     * @param collateralAmount The amount of collateral
     * @return The equivalent USDe amount (18 decimals)
     */
    function _convertToUsdeAmount(address collateralAsset, uint256 collateralAmount) internal view returns (uint256) {
        uint8 collateralDecimals = IERC20Metadata(collateralAsset).decimals();

        if (collateralDecimals == 18) {
            return collateralAmount;
        } else if (collateralDecimals < 18) {
            // Scale up (e.g., USDC 6 decimals -> 18 decimals)
            return collateralAmount * (10 ** (18 - collateralDecimals));
        } else {
            // Scale down (shouldn't happen with standard stablecoins)
            return collateralAmount / (10 ** (collateralDecimals - 18));
        }
    }

    /**
     * @notice Convert USDe amount to collateral amount (denormalize decimals)
     * @param collateralAsset The collateral asset address
     * @param usdeAmount The amount of USDe (18 decimals)
     * @return The equivalent collateral amount
     */
    function _convertToCollateralAmount(address collateralAsset, uint256 usdeAmount) internal view returns (uint256) {
        uint8 collateralDecimals = IERC20Metadata(collateralAsset).decimals();

        if (collateralDecimals == 18) {
            return usdeAmount;
        } else if (collateralDecimals < 18) {
            // Scale down (e.g., 18 decimals -> USDC 6 decimals)
            return usdeAmount / (10 ** (18 - collateralDecimals));
        } else {
            // Scale up (shouldn't happen with standard stablecoins)
            return usdeAmount * (10 ** (collateralDecimals - 18));
        }
    }

    /**
     * @notice Set max mint per block (internal)
     * @param _maxMintPerBlock New max mint per block
     */
    function _setMaxMintPerBlock(uint256 _maxMintPerBlock) internal {
        uint256 oldMax = maxMintPerBlock;
        maxMintPerBlock = _maxMintPerBlock;
        emit MaxMintPerBlockChanged(oldMax, _maxMintPerBlock);
    }

    /**
     * @notice Set max redeem per block (internal)
     * @param _maxRedeemPerBlock New max redeem per block
     */
    function _setMaxRedeemPerBlock(uint256 _maxRedeemPerBlock) internal {
        uint256 oldMax = maxRedeemPerBlock;
        maxRedeemPerBlock = _maxRedeemPerBlock;
        emit MaxRedeemPerBlockChanged(oldMax, _maxRedeemPerBlock);
    }

    /* --------------- OVERRIDES --------------- */

    /**
     * @notice Override ERC4626 withdraw to enforce cooldown - disabled
     * @dev Use cooldownRedeem/completeRedeem instead
     */
    function withdraw(uint256, address, address) public pure override returns (uint256) {
        revert("Use cooldownRedeem");
    }

    /**
     * @notice Override ERC4626 redeem to enforce cooldown - disabled
     * @dev Use cooldownRedeem/completeRedeem instead
     */
    function redeem(uint256, address, address) public pure override returns (uint256) {
        revert("Use cooldownRedeem");
    }

    /// @dev Override decimals to ensure 18 decimals
    function decimals() public pure override(ERC4626, ERC20) returns (uint8) {
        return 18;
    }

    /**
     * @notice Prevent renouncing ownership
     */
    function renounceRole(bytes32 role, address account) public virtual override {
        if (role == DEFAULT_ADMIN_ROLE) revert CantRenounceOwnership();
        super.renounceRole(role, account);
    }
}

contracts/usde/USDeComposer.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.22;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { VaultComposerSync } from "@layerzerolabs/ovault-evm/contracts/VaultComposerSync.sol";
import { OFTComposeMsgCodec } from "@layerzerolabs/oft-evm/contracts/libs/OFTComposeMsgCodec.sol";
import { IOFT, SendParam, MessagingFee } from "@layerzerolabs/oft-evm/contracts/interfaces/IOFT.sol";
import { IVaultComposerSync } from "@layerzerolabs/ovault-evm/contracts/interfaces/IVaultComposerSync.sol";

interface IUSDe {
    function mintWithCollateral(
        address collateralAsset,
        uint256 collateralAmount
    ) external returns (uint256 usdeAmount);
}

/**
 * @title USDeComposer
 * @notice Composer that routes deposits through USDe.mintWithCollateral instead of ERC4626.deposit
 * @dev Collateral flows: user sends collateralAsset to composer  composer approves USDe 
 *      calls USDe.mintWithCollateralFor(collateralAsset, amount, address(this))  sends shares cross-chain
 * @dev Cross-chain deposits of collateral require a collateral OFT and base composer support; this contract
 *      overrides local deposit flow via depositAndSend().
 */
contract USDeComposer is VaultComposerSync {
    using SafeERC20 for IERC20;
    using OFTComposeMsgCodec for bytes;

    /// @notice Collateral asset to use for minting (e.g., USDC)
    address public immutable collateralAsset;
    /// @notice OFT contract for the collateral asset (e.g., Stargate USDC OFT)
    address public immutable collateralAssetOFT;

    // Debug events
    event DebugLzComposeStart(address composeSender, bytes32 guid, uint256 msgValue);
    event DebugValidationPassed();
    event DebugMessageDecoded(bytes32 composeFrom, uint256 amount, uint256 composeMsgLength);
    event DebugHandleComposeStart(address oftIn, bytes32 composeFrom, uint256 amount);
    event DebugSendParamDecoded(uint32 dstEid, bytes32 to, uint256 amountLD, uint256 minAmountLD);
    event DebugMsgValueCheck(uint256 minMsgValue, uint256 actualMsgValue);
    event DebugProcessingAssetOFT();
    event DebugProcessingShareOFT();
    event DebugProcessingCollateralAsset();
    event DebugDepositCollateralAndSendStart(uint256 assetAmount);
    event DebugDepositCollateralAndSendComplete(uint256 shareAmount);
    event DebugHandleComposeComplete();
    event DebugLzComposeComplete();
    event DebugError(bytes errorData);
    event DepositedUSDe(
        address share,
        address recipient,
        uint32 dstEid,
        bytes32 to,
        uint256 amountLD,
        uint256 minAmountLD,
        bytes extraOptions,
        bytes composeMsg,
        bytes oftCmd
    );

    /**
     * @param _vault The USDe contract implementing ERC4626
     * @param _assetOFT The asset OFT address expected by base (must match vault.asset(), i.e., MCT)
     * @param _shareOFT The USDe OFT adapter contract for cross-chain share transfers
     * @param _collateralAsset The ERC20 collateral to mint with (e.g., USDC)
     */
    constructor(
        address _vault,
        address _assetOFT,
        address _shareOFT,
        address _collateralAsset,
        address _collateralAssetOFT
    ) VaultComposerSync(_vault, _assetOFT, _shareOFT) {
        collateralAsset = _collateralAsset;
        collateralAssetOFT = _collateralAssetOFT;
        // Approve collateral to its OFT for potential refunds
        IERC20(collateralAsset).approve(collateralAssetOFT, type(uint256).max);
    }

    /**
     * @dev Collateral deposit path: mints shares using USDe.mintWithCollateralFor and sends them
     */
    function _depositCollateralAndSend(
        bytes32 _depositor,
        uint256 _assetAmount,
        SendParam memory _sendParam,
        address _refundAddress
    ) internal {
        emit DebugDepositCollateralAndSendStart(_assetAmount);

        // Approve USDe to pull collateral from this composer
        IERC20(collateralAsset).forceApprove(address(VAULT), _assetAmount);
        // Mint USDe to this contract
        uint256 shareAmount = IUSDe(address(VAULT)).mintWithCollateral(collateralAsset, _assetAmount);
        _assertSlippage(shareAmount, _sendParam.minAmountLD);

        _sendParam.amountLD = shareAmount;
        _sendParam.minAmountLD = 0;

        _send(SHARE_OFT, _sendParam, _refundAddress);
        emit DepositedUSDe(
            SHARE_OFT,
            _refundAddress,
            _sendParam.dstEid,
            _sendParam.to,
            _sendParam.amountLD,
            _sendParam.minAmountLD,
            _sendParam.extraOptions,
            _sendParam.composeMsg,
            _sendParam.oftCmd
        );

        emit DebugDepositCollateralAndSendComplete(shareAmount);
    }

    /**
     * @notice Override lzCompose to accept collateralAsset as a valid compose sender
     * @dev This allows the composer to handle cross-chain deposits of collateral assets (e.g., USDC)
     * @param _composeSender The OFT contract address used for refunds, can be ASSET_OFT, SHARE_OFT, or collateralAsset
     * @param _guid LayerZero's unique tx id (created on the source tx)
     * @param _message Decomposable bytes object into [composeHeader][composeMessage]
     */
    function lzCompose(
        address _composeSender, // The OFT used on refund, also the vaultIn token.
        bytes32 _guid,
        bytes calldata _message, // expected to contain a composeMessage = abi.encode(SendParam hopSendParam,uint256 minMsgValue)
        address /*_executor*/,
        bytes calldata /*_extraData*/
    ) external payable override {
        emit DebugLzComposeStart(_composeSender, _guid, msg.value);

        if (msg.sender != ENDPOINT) revert OnlyEndpoint(msg.sender);
        if (
            _composeSender != ASSET_OFT &&
            _composeSender != SHARE_OFT &&
            _composeSender != collateralAsset &&
            _composeSender != collateralAssetOFT
        ) {
            revert OnlyValidComposeCaller(_composeSender);
        }

        emit DebugValidationPassed();

        bytes32 composeFrom = _message.composeFrom();
        uint256 amount = _message.amountLD();
        bytes memory composeMsg = _message.composeMsg();

        emit DebugMessageDecoded(composeFrom, amount, composeMsg.length);

        /// @dev try...catch to handle the compose operation. if it fails we refund the user
        try this._handleComposeInternal{ value: msg.value }(_composeSender, composeFrom, composeMsg, amount) {
            emit Sent(_guid);
            emit DebugLzComposeComplete();
        } catch (bytes memory _err) {
            emit DebugError(_err);

            /// @dev A revert where the msg.value passed is lower than the min expected msg.value is handled separately
            /// This is because it is possible to re-trigger from the endpoint the compose operation with the right msg.value
            if (bytes4(_err) == InsufficientMsgValue.selector) {
                assembly {
                    revert(add(32, _err), mload(_err))
                }
            }

            _refund(_composeSender, _message, amount, tx.origin);
            emit Refunded(_guid);
        }
    }

    /**
     * @notice Internal function to handle compose operations
     * @dev This function can only be called by self (self-call restriction)
     */
    function _handleComposeInternal(
        address _oftIn,
        bytes32 _composeFrom,
        bytes memory _composeMsg,
        uint256 _amount
    ) external payable {
        emit DebugHandleComposeStart(_oftIn, _composeFrom, _amount);

        /// @dev Can only be called by self
        if (msg.sender != address(this)) revert OnlySelf(msg.sender);

        /// @dev SendParam defines how the composer will handle the user's funds
        /// @dev The minMsgValue is the minimum amount of msg.value that must be sent, failing to do so will revert and the transaction will be retained in the endpoint for future retries
        (SendParam memory sendParam, uint256 minMsgValue) = abi.decode(_composeMsg, (SendParam, uint256));

        emit DebugSendParamDecoded(sendParam.dstEid, sendParam.to, sendParam.amountLD, sendParam.minAmountLD);
        emit DebugMsgValueCheck(minMsgValue, msg.value);

        if (msg.value < minMsgValue) revert InsufficientMsgValue(minMsgValue, msg.value);

        if (_oftIn == ASSET_OFT) {
            emit DebugProcessingAssetOFT();
            super._depositAndSend(_composeFrom, _amount, sendParam, tx.origin);
        } else if (_oftIn == SHARE_OFT) {
            emit DebugProcessingShareOFT();
            super._redeemAndSend(_composeFrom, _amount, sendParam, tx.origin);
        } else if (_oftIn == collateralAssetOFT) {
            emit DebugProcessingCollateralAsset();
            _depositCollateralAndSend(_composeFrom, _amount, sendParam, tx.origin);
        } else {
            revert OnlyValidComposeCaller(_oftIn);
        }

        emit DebugHandleComposeComplete();
    }

    /**
     * @notice Override _refund to handle collateral asset via its OFT
     */
    function _refund(address _oft, bytes calldata _message, uint256 _amount, address _refundAddress) internal override {
        if (_oft == collateralAsset) {
            SendParam memory refundSendParam;
            refundSendParam.dstEid = OFTComposeMsgCodec.srcEid(_message);
            refundSendParam.to = OFTComposeMsgCodec.composeFrom(_message);
            refundSendParam.amountLD = _amount;

            IOFT(collateralAssetOFT).send{ value: msg.value }(
                refundSendParam,
                MessagingFee(msg.value, 0),
                _refundAddress
            );
        } else {
            super._refund(_oft, _message, _amount, _refundAddress);
        }
    }
}

contracts/usde/USDeRedeemSilo.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title USDeRedeemSilo
 * @notice Holds locked USDe tokens during redemption cooldown period
 */
contract USDeRedeemSilo {
    /// @notice Error when caller is not the vault
    error OnlyVault();

    address immutable VAULT;
    IERC20 immutable USDE;

    constructor(address vault, address usde) {
        VAULT = vault;
        USDE = IERC20(usde);
    }

    modifier onlyVault() {
        if (msg.sender != VAULT) revert OnlyVault();
        _;
    }

    /// @notice Withdraw USDe from silo (only callable by vault)
    function withdraw(address to, uint256 amount) external onlyVault {
        USDE.transfer(to, amount);
    }
}
